SELECT tau.id,tau.username,tau.name,tar.name AS role_name,tau.created_at,tau.updated_at FROM admin_users tau LEFT JOIN admin_role_users taru ON tau.id=taru.user_id LEFT JOIN admin_roles tar ON taru.role_id=tar.id WHERE 1=1
AND tau.id = #{id}
ORDER BY tau.id ASC

select

from admin_users
where username = #{username,jdbcType=VARCHAR} and  password = #{password,jdbcType=VARCHAR}
